mapper.insert:
INSERT INTO board (
	idx
	,writer
	,title
	,content
	,create_date
	,hit
	) VALUES (
		#{idx}
		,#{writer}
		,#{title}
		,#{content}
		,NOW()
		,default

) ON DUPLICATE KEY
UPDATE
	title = #{title}
	,content = #{content}
	,writer = #{writer}

INSERT INTO boardfile (file_original_name
					 , file_stored_name
					 , file_content_type
					 , file_upload_date
					 , file_path
					 , idx)
VALUES (#{fileOriginalName}
		, #{fileStoredName}
		, #{fileContentType}
		, NOW()
		, #{filePath}
		, #{idx}
)

mapper.select:
SELECT count(*) 
FROM boardlike 
WHERE board_idx = #{boardIdx} 
	AND userId = #{userId}

mapper.delete:
DELETE FROM boardLike
WHERE board_idx = #{boardIdx} 
AND userId = #{userId}
